create database my_list;

CREATE TABLE `animes` (
  `id_anime` int(11) NOT NULL PRIMARY KEY AUTO_INCREMENT,
  `nome_anime` varchar(300) NOT NULL,
  `nota` varchar(5) NOT NULL,
  `sinopse` text NOT NULL,
  `genero` varchar(500) NOT NULL,
  `dt_lancamento` date NOT NULL,
  `dt_cadastro` datetime NOT NULL DEFAULT current_timestamp(),
  `qnt_eps` int(11) NOT NULL,
  `status_anime` varchar(30) NOT NULL,
  `link` varchar(500) NOT NULL
);

CREATE TABLE `eps_anime` (
  `id_ep_anime` int(11) NOT NULL PRIMARY KEY AUTO_INCREMENT,
  `id_anime` int(11) NOT NULL,
  `numero_ep` int(11) NOT NULL,
  `nome_ep` varchar(200) NOT NULL,
  `dt_lancamento` date NOT NULL,
  `dt_cadastro` datetime NOT NULL DEFAULT current_timestamp()
);

CREATE TABLE `eps_assistidos` (
  `id_ep_assistido` int(11) NOT NULL PRIMARY KEY AUTO_INCREMENT,
  `id_itemLista` int(11) NOT NULL,
  `id_ep_anime` int(11) NOT NULL,
  `dt_cadastro` datetime NOT NULL DEFAULT current_timestamp(),
  `dt_assistido` date NOT NULL
);

CREATE TABLE `lista` (
  `id_lista` int(11) NOT NULL PRIMARY KEY AUTO_INCREMENT,
  `id_usuario` int(11) NOT NULL,
  `dt_cadastro` datetime NOT NULL DEFAULT current_timestamp()
);

CREATE TABLE `item_lista` (
  `id_itemLista` int(11) NOT NULL PRIMARY KEY AUTO_INCREMENT,
  `id_lista` int(11) NOT NULL,
  `id_anime` int(11) NOT NULL,
  `adicionado_em` datetime NOT NULL DEFAULT current_timestamp(),
  `status` varchar(50) NOT NULL
);

CREATE TABLE `log_crawler_atualizareps` (
  `id_log_crawler_atualizareps` int(11) NOT NULL PRIMARY KEY AUTO_INCREMENT,
  `id_anime_alterado` int(11) NOT NULL,
  `animes_novos` int(11) NOT NULL,
  `mensagem_log` varchar(500) NOT NULL,
  `dt_cadastro` datetime NOT NULL DEFAULT current_timestamp()
);

CREATE TABLE `usuarios` (
  `id_usuario` int(11) NOT NULL PRIMARY KEY AUTO_INCREMENT,
  `nome_usuario` varchar(100) NOT NULL,
  `email` varchar(150) NOT NULL,
  `senha` varchar(200) NOT NULL,
  `dt_cadastro` datetime NOT NULL DEFAULT current_timestamp()
);





